// src/services/holdsService.js
// Core holds and reservations management

import { db } from '../firebase-config';
import { 
  collection, 
  doc, 
  getDocs, 
  getDoc,
  addDoc, 
  updateDoc,
  deleteDoc,
  query, 
  where,
  orderBy,
  Timestamp,
  runTransaction 
} from 'firebase/firestore';
import { 
  validateHoldRequest, 
  validateReservationRequest,
  getUserProfile 
} from './remoteSyncService';

// ============================================
// HOLDS MANAGEMENT
// ============================================

export async function placeHold(userId, itemId) {
  try {
    const validation = await validateHoldRequest(userId, itemId);
    if (!validation.valid) {
      return { success: false, error: validation.reason };
    }

    // Use transaction to atomically get next position and create hold
    const holdRef = await runTransaction(db, async (transaction) => {
      // Get all existing holds for this item
      const holdsQuery = query(
        collection(db, 'holds'),
        where('itemId', '==', itemId),
        orderBy('position', 'asc')
      );
      const holdsSnapshot = await getDocs(holdsQuery);

      const nextPosition = holdsSnapshot.size + 1;

      const holdData = {
        userId,
        itemId,
        status: 'waiting',
        position: nextPosition,
        createdAt: Timestamp.now(),
        notified: false
      };

      const newHoldRef = doc(collection(db, 'holds'));
      transaction.set(newHoldRef, holdData);

      return { ref: newHoldRef, data: holdData };
    });

    console.log('Hold placed:', holdRef.ref.id);
    return { success: true, holdId: holdRef.ref.id, ...holdRef.data };
  } catch (error) {
    console.error('Error placing hold:', error);
    return { success: false, error: error.message };
  }
}

export async function placeReservation(userId, itemId) {
  try {
    const validation = await validateReservationRequest(userId, itemId);
    if (!validation.valid) {
      return { success: false, error: validation.reason };
    }

    const reservationData = {
      userId,
      itemId,
      status: 'active',
      expiresAt: Timestamp.fromDate(new Date(Date.now() + 7 * 24 * 60 * 60 * 1000)),
      createdAt: Timestamp.now()
    };

    const resRef = await addDoc(collection(db, 'reservations'), reservationData);

    console.log('Reservation placed:', resRef.id);
    return { success: true, reservationId: resRef.id, ...reservationData };
  } catch (error) {
    console.error('Error placing reservation:', error);
    return { success: false, error: error.message };
  }
}

export async function getUserHolds(userId) {
  try {
    const holdsCol = collection(db, 'holds');
    const q = query(holdsCol, where('userId', '==', userId));
    const snapshot = await getDocs(q);

    const holds = snapshot.docs.map(doc => ({
      id: doc.id,
      ...doc.data()
    }));

    const userProfile = await getUserProfile(userId);

    return {
      success: true,
      holds,
      userInfo: userProfile.success ? userProfile.data : null
    };
  } catch (error) {
    console.error('Error fetching user holds:', error);
    return { success: false, error: error.message };
  }
}

export async function getUserReservations(userId) {
  try {
    const resCol = collection(db, 'reservations');
    const q = query(resCol, where('userId', '==', userId));
    const snapshot = await getDocs(q);

    const reservations = snapshot.docs.map(doc => ({
      id: doc.id,
      ...doc.data()
    }));

    return { success: true, reservations };
  } catch (error) {
    console.error('Error fetching user reservations:', error);
    return { success: false, error: error.message };
  }
