export async function getItemHolds(itemId) {
  try {
    const holdsCol = collection(db, 'holds');
    const q = query(
      holdsCol, 
      where('itemId', '==', itemId),
      orderBy('position', 'asc')
    );
    const snapshot = await getDocs(q);

    return snapshot.docs.map(doc => ({
      id: doc.id,
      ...doc.data()
    }));
  } catch (error) {
    console.error('Error fetching item holds:', error);
    return [];
  }
}

export async function cancelHold(holdId) {
  try {
    // Use transaction to atomically delete hold and reorder queue
    await runTransaction(db, async (transaction) => {
      const holdRef = doc(db, 'holds', holdId);
      const holdSnap = await transaction.get(holdRef);

      if (!holdSnap.exists()) {
        throw new Error('Hold not found');
      }

      const holdData = holdSnap.data();
      const itemId = holdData.itemId;

      // Delete the hold
      transaction.delete(holdRef);

      // Get all remaining holds for this item and reorder
      const holdsQuery = query(
        collection(db, 'holds'),
        where('itemId', '==', itemId),
        orderBy('position', 'asc')
      );
      const holdsSnapshot = await getDocs(holdsQuery);

      // Reorder positions starting from 1
      let position = 1;
      holdsSnapshot.docs.forEach((holdDoc) => {
        if (holdDoc.id !== holdId) {
          transaction.update(holdDoc.ref, { position: position++ });
        }
      });
    });

    console.log('Hold cancelled:', holdId);
    return { success: true };
  } catch (error) {
    console.error('Error cancelling hold:', error);
    return { success: false, error: error.message };
  }
}

export async function cancelReservation(reservationId) {
  try {
    const resRef = doc(db, 'reservations', reservationId);
    await updateDoc(resRef, { status: 'cancelled' });

    console.log('Reservation cancelled:', reservationId);
    return { success: true };
  } catch (error) {
    console.error('Error cancelling reservation:', error);
    return { success: false, error: error.message };
  }
}

export async function updateHoldStatus(holdId, status, notified = false) {
  try {
    const holdRef = doc(db, 'holds', holdId);
    await updateDoc(holdRef, { status, notified });

    console.log('Hold status updated:', holdId, status);
    return { success: true };
  } catch (error) {
    console.error('Error updating hold status:', error);
    return { success: false, error: error.message };
  }
}

// ============================================
// HELPER FUNCTIONS
// ============================================

// Note: getNextQueuePosition and reorderHoldQueue are now handled
// within transactions in placeHold() and cancelHold() to prevent race conditions

// ============================================
// ADMIN FUNCTIONS
// ============================================

export async function getAllHolds() {
  try {
    const snapshot = await getDocs(collection(db, 'holds'));
    return snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
  } catch (error) {
    console.error('Error fetching all holds:', error);
    return [];
  }
}

export async function getAllReservations() {
  try {
    const snapshot = await getDocs(collection(db, 'reservations'));
    return snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
  } catch (error) {
    console.error('Error fetching all reservations:', error);
    return [];
  }
}

export async function expireOldReservations() {
  try {
    const resCol = collection(db, 'reservations');
    const snapshot = await getDocs(resCol);
    const now = Timestamp.now();

    const expired = [];

    for (const docSnap of snapshot.docs) {
      const data = docSnap.data();
      if (data.status === 'active' && data.expiresAt && data.expiresAt.toMillis() < now.toMillis()) {
        await updateDoc(doc(db, 'reservations', docSnap.id), { status: 'expired' });
        expired.push(docSnap.id);
      }
    }

    console.log('Expired reservations:', expired.length);
    return { success: true, expiredCount: expired.length };
  } catch (error) {
    console.error('Error expiring reservations:', error);
    return { success: false, error: error.message };
  }
}
