          throw new Error('You already have a hold on this item');
        }

        // Create the hold
        transaction.set(newHoldRef, holdData);
      });
    } catch (error) {
      // If transaction fails due to concurrent modification, retry once
      if (error.message.includes('transaction') || error.message.includes('concurrent')) {
        // Retry with fresh position calculation
        const retryHolds = await getItemHolds(itemId);
        holdData.position = retryHolds.length + 1;
        await runTransaction(db, async transaction => {
          const existingHoldQuery = query(collection(db, 'holds'), where('userId', '==', userId), where('itemId', '==', itemId));
